import React from 'react';
import {Image, StyleSheet, View} from 'react-native'
import AppText from '../components/AppText';
import ButtonTags from '../components/ButtonTags';
import ListItemSeparator from '../components/ListItemSeparator';
import Screen from '../components/Screen';
import SquareButtons from '../components/SquareButtons';
import colors from '../config/colors';

function AccountDetailsScreen(props) {
    return (
        <Screen>
            <View style={styles.container}>
                <View style={styles.subcont}>
                    <Image style={styles.image}source={require('../assets/richard.jpg')}/>
                </View>
                <View style={styles.namecont}>
                    <AppText style={styles.name}>Richard Hendricks</AppText>
                </View>
                <View style={styles.courselvl}>
                    <AppText style={styles.course}>Year IV | Economics</AppText>
                </View>
                <View style={styles.buttonContainer}>
                    <ButtonTags textt='Mathematics'/>
                    <ButtonTags textt='English'/>
                    <ButtonTags textt='Econometrics'/>
                    <ButtonTags textt='Statistics'/>
                </View>
                <ListItemSeparator />
                <View style={styles.messageCont}>
                    <AppText style={styles.message}>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                    sed do eiusmod tempor incididunt ut labore et dolore magna 
                    aliqua. Ut enim ad minim veniam, quis nostrud exercitation 
                    ullamco laboris nisi ut aliquip ex ea commodo consequat. 
                    Duis aute irure dolor in reprehenderit in voluptate velit 
                    esse cillum dolore eu fugiat nulla pariatur. Excepteur sint 
                    occaecat cupidatat non proident, sunt in culpa qui officia 
                    deserunt mollit anim id est laborum. Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                    sed do eiusmod tempor incididunt ut labore et dolore magna 
                    aliqua. 
                    </AppText>
                </View>
                <ListItemSeparator />
                <View style={styles.sendMessageCont}>
                    <View style={styles.sendSubCont}> 
                        <AppText style={styles.messageText}>

                        </AppText>
                    </View>
                    <View style={styles.button}>
                        <SquareButtons title='send'/>
                    </View>
                </View>
            </View>
        </Screen>
    );
}

const styles = StyleSheet.create({
    image: {
        width: 100,
        height: 120,
        borderRadius: 50,
        alignItems: 'center'

    },
    sendMessageCont: {
        flexDirection: 'row',
        paddingTop: 50,
        justifyContent: 'center'
    },
    sendSubCont: {
        width: 250,
        height: 38,
        borderRadius: 7,
        backgroundColor: '#EDEDED',

    },
    messageCont: {
        paddingBottom: 30,
        paddingTop: 20,
        paddingLeft: 20,
        paddingRight: 20
    },
    buttonContainer: {
        flexDirection: 'row',
        paddingBottom: 20
    },
    message: {
        fontSize: 16
    },
    container: {
        padding: 20
    },
    subcont: {
        alignItems: 'center'
    },
    namecont: {
        alignItems: 'center',
        paddingTop: 17,

    },
    course: {
        fontSize: 13
    },

    name: {
        fontSize: 15,
        fontWeight: '700',
        alignItems: 'center'

    },
    courselvl: {
        alignItems: 'center',
        paddingBottom: 25

    },
    button: {
        paddingLeft: 20
    }
})

export default AccountDetailsScreen;
