import React from 'react';
import { Image, StyleSheet, View } from 'react-native';
import App from '../../App';
import AppText from '../components/AppText';
import Card from '../components/Card';
import Screen from '../components/Screen';
import colors from '../config/colors';

function HomeScreen(props) {
    return (
        <Screen style={styles.screenCont}>
            <View style={styles.screen}>
            <View style={styles.welcomeCard}>
                <View>
                    <AppText style={styles.greeting}>Good Day,</AppText>
                    <AppText style={styles.greetings}>Uzoamaka</AppText>
                </View>
                <View style={styles.imageContainer}>
                    <Image style={styles.image} source={require('../assets/reading.png')}/>
                </View>
            </View>
                <View style={styles.courseCont}>
                    <AppText style={styles.course}>courses</AppText>
                    <AppText>______</AppText>
                </View>
                <View>
                    <View style={styles.boxCont}>
                        <Card coursetitle = 'Economics' />
                        <Card coursetitle = 'Accounting'/>
                    </View>
                    <View style={styles.boxCont}>
                        <Card coursetitle = 'Computer Science' />
                        <Card coursetitle = 'Bus Admin'/>
                    </View>
                    <View style={styles.boxCont}>
                        <Card coursetitle = 'Mass Comm.' />
                        <Card coursetitle = 'Accounting'/>
                    </View>
                </View>
            </View>
        </Screen>
    );
}
const styles = StyleSheet.create({
    welcomeCard: {
        height: 220,
        width: 380,
        backgroundColor: '#6DFFCB',
        borderRadius: 9,
        padding: 20,
        paddingBottom: 50,
        shadowOffset: {height: 8, },
        shadowColor: "grey",
        shadowOpacity: 0.3,
    },
    screen: {
        padding: 20,
        alignItems: 'center'
    },
    greeting: {
        fontWeight: '500',
        fontSize: 26
    },
    greetings: {
        fontWeight: '500',
        fontSize: 20
    },
    image: {
        height: 130,
        width: 160,
    },
    imageContainer: {
        paddingLeft: 170

    },
    course: {
        fontSize: 25,
        fontWeight: '300'
    },
    courseCont: {
        paddingTop: 40,
        alignItems: 'center'
    },
    boxCont: {
        paddingTop: 25,
        flexDirection: 'row',
        justifyContent: 'space-evenly',
        width: '100%'
    }

})
export default HomeScreen;
